import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Msteams from '../../assets/img/microsoft-teams-logo-0.png';
//import Sharepoint from '../../assets/img/Microsoft_Office_SharePoint_(2019–present).svg.png'
import { useState } from "react";
import { CardGroup } from 'react-bootstrap';
import styled from "styled-components";

export const BasicExample = () => {
    const [ImageSelectedPrevious, setImageSelectedPrevious] = useState(null);
    const changeImage = (e) => {
      console.log(e.target.files);
      if (e.target.files[0] !== undefined) {
        const reader = new FileReader();
        reader.readAsDataURL(e.target.files[0]);
        reader.onload = (e) => {
          e.preventDefault();
          setImageSelectedPrevious(e.target.result); // le damos el binario de la imagen para mostrarla en pantalla
        };
      }
    };
  return (
    <CardGroup className='cardTeams'> 
        <Card className='cardTeams'>
        <Card.Img variant="top" src={Msteams}/>
        <Card.Body>
            <Card.Title>Microsoft Teams</Card.Title>
            <Card.Text>
            Realiza llamadas al instante con Microsoft Teams.
            </Card.Text>
            <Button variant="primary">Conectar a videollamada</Button>
        </Card.Body>
        </Card>

        <Card className='cardTeams'>
        <StyleDragArea>
        <br />
        <div className="image-upload-wrap">
          <input
            className="file-upload-input"
            type="file"
            accept="image/*"
            multiple
            onChange={(e) => {
              changeImage(e);
            }}
          />
          <div className="text-information">
            <h3>S</h3>
          </div>
        </div>

        <div className="center">
          <img
            src={ImageSelectedPrevious}
            alt=""
            height="150px"
            width="250px"
          />
        </div>
      </StyleDragArea>
        </Card>

    </CardGroup>
  );
}

const StyleDragArea = styled.div`
  .center {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .file-upload-content {
    display: none;
    text-align: center;
  }

  .file-upload-input {
    position: absolute;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 100%;
    outline: none;
    opacity: 0;
    cursor: pointer;
  }

  .image-upload-wrap {
    position: relative;
    height: 100px;
    border: 4px solid #d0d7de;
    margin-left: 10px;
    margin-right: 10px;
  }

  .image-upload-wrap:hover {
    background-color: transparent;
    border: 4px dashed #d0d7de;
  }
  .text-information {
    margin-top: 30px;
    text-align: center;
  }
`;
