import express, { Router } from 'express';
const App        = express();
import { json } from 'body-parser';
App.use(json());
const router = Router()


// INICIO DE RUTAS PARA LAS VISTAS

// ----------------------------------------------------------------
// RUTAS PARA VISTAS DE CLIENTES
// ----------------------------------------------------------------

// Login
router.get('/', async (req, res) => {
    
    res.render('login')
});
